      const responseData = await response.json()

      const loadedMeals =[]
      for(const key in responseData){
        loadedMeals.push({
          id:key,
          name:responseData[key].name,
          description:responseData[key].name,
          name:responseData[key].name,

        })
      }
    }
    fetchMeals()
  },[])

  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      name={meal.name}
